class Solution {
public:
    vector<vector<int>> updateMatrix(vector<vector<int>>& mat) {
        int n = mat.size(), m = mat[0].size();
        vector<vector<int>> ans(n, vector<int>(m, 1e9));
        for(int i = 0; i < n;i++){
            for(int j = 0; j < m;j++){
                if(mat[i][j] == 0)ans[i][j] = 0;
            }
        }
        for(int i = 0; i < n;i++){
            for(int j = 0; j < m;j++){
                if(mat[i][j] == 0)continue;
                vector<int> arr = {-1, 0, 1, 0, -1};
                for(int k =0 ;k < 4;k++){
                    int _x = i + arr[k];
                    int _y = j + arr[k + 1];
                    if(_x >=0 && _x < n && _y>= 0 && _y < m){
                        ans[i][j] = min(ans[i][j], ans[_x][_y] + 1);
                    }
                }
            }
        }
        for(int i = n-1; i >=0;i--){
            for(int j = m-1; j >=0;j--){
                if(mat[i][j] == 0)continue;
                vector<int> arr = {-1, 0, 1, 0, -1};
                for(int k =0 ;k < 4;k++){
                    int _x = i + arr[k];
                    int _y = j + arr[k + 1];
                    if(_x >=0 && _x < n && _y>= 0 && _y < m){
                        ans[i][j] = min(ans[i][j], ans[_x][_y] + 1);
                    }
                }
            }
        }
        return ans;
    }
};
