class Solution {
public:
    int threeSumClosest(vector<int> &nums, int target)
    {
        sort(nums.begin(), nums.end());
        int n = nums.size();
        int _min = INT_MAX;
        for (int i = 0; i < n - 2; i++)
        {
            int j = i + 1;
            int k = n - 1;
            while (j < k)
            {
                int sum = nums[i] + nums[j] + nums[k];
                if (sum == target)
                {
                    return sum;
                }
                else if (sum < target)
                {
                    j++;
                }
                else
                {
                    k--;
                }

                if (abs(target - sum) < abs(_min))
                {
                    _min = sum - target;
                }
            }
        }
        return _min + target;
    }
};
