class Solution {
public:   
   vector<vector<int>> threeSum(vector<int> &nums)
{
    int n = nums.size();
    vector<vector<int>> arr;
    if (n < 3)
    {
        return arr;
    }
    sort(nums.begin(), nums.end());
    int previ = nums[0] - 1;
    int prevj = nums[1] - 1;
    for (int i = 0; i < n - 2; i++)
    {
        if (previ == nums[i])
        {
            continue;
        }

        previ = nums[i] - 1;
        prevj = nums[i + 1] - 1;
        int j = i + 1;
        int k = n - 1;
        if (previ == nums[i])
        {
            continue;
        }
        while (j < k)
        {
            if (prevj == nums[j])
            {
                j++;
                continue;
            }
            if (nums[i] + nums[j] + nums[k] == 0)
            {
                vector<int> temp;
                temp.push_back(nums[i]);
                temp.push_back(nums[j]);
                temp.push_back(nums[k]);
                arr.push_back(temp);
                prevj = nums[j];
                k--;
            }
            else if (nums[i] + nums[j] + nums[k] > 0)
            {
                k--;
            }
            else
            {
                prevj = nums[j];
                j++;
            }
        }
        previ = nums[i];
    }
    return arr;
}
};
