class Solution {
public:
    vector<vector<int>> fourSum(vector<int> &nums, int target)
    {
        set<vector<int>> st;
        int n = nums.size();
        sort(nums.begin(), nums.end());

        for(int i=0;i<n;i++){
            for(int j = i + 3;j<n;j++){
                int a = nums[i];
                int b = nums[j];
                int start = i + 1, end = j - 1;
                while(start < end){
                    long long sum = nums[start]*1LL + nums[end]*1LL + a*1LL + b*1LL;
                    if(sum== target){
                        vector<int> temp = {a,nums[start], nums[end], b};
                        st.insert(temp);
                        start++;
                    }
                    else if(sum < target){
                        start ++;
                    }else{
                        end --;
                    }
                }
            }
        }
        vector<vector<int>> ans;
        for(auto i : st){
            ans.push_back(i);
        }
        return ans;
    }
};
