class Solution
{
public:
    string addBinary(string a, string b)
    {
        reverse(a.begin(), a.end());
        reverse(b.begin(), b.end());
        int i = 0, j = 0, carry = 0;
        string ans = "";
        while (i < a.length() && i < b.length())
        {
            int temp = (a[i] - '0') + (b[i] - '0') + carry;
            temp > 1 ? carry = 1 : carry = 0;
            char c = (temp % 2 + '0');
            ans.push_back(c);
            i++;
        }
        while (i < a.length())
        {
            int temp = (a[i] - '0') + carry;
            temp > 1 ? carry = 1 : carry = 0;
            char c = (temp % 2 + '0');
            ans.push_back(c);
            i++;
        }
        while (i < b.length())
        {
            int temp = (b[i] - '0') + carry;
            temp > 1 ? carry = 1 : carry = 0;
            char c = (temp % 2 + '0');
            ans.push_back(c);
            i++;
        }
        if (carry != 0)
        {
            ans.push_back('1');
        }
        reverse(ans.begin(), ans.end());
        return ans;
    }
};
