class Solution {
public:
    string addStrings(string num1, string num2) {
        string temp = "";
        int carry = 0;
        int i = 0, j = 0;
        reverse(num1.begin(), num1.end());
        reverse(num2.begin(), num2.end());
        while(i < num1.length() && j < num2.length()){
            int a = num1[i++] - '0';
            int b = num2[j++] - '0';
            int c = a + b + carry;
            cout<<a<<" "<<b<<" "<<c<<endl;
            carry = c/10;
            temp.push_back(c%10 + '0');
        }
        while(i < num1.length()){
            int a = num1[i++] - '0';
            int c = a + carry;
            carry = c/10;
            temp.push_back(c%10 + '0');
        }
        while(j < num2.length()){
            int a = num2[j++] - '0';
            int c = a + carry;
            carry = c/10;
            temp.push_back(c%10 + '0');
        }
        while(carry != 0){
            temp.push_back(carry%10 + '0');
            carry/=10;
        }
        reverse(temp.begin(), temp.end());
        return temp;
    }
};
