class Solution {
public:
    vector<int> addToArrayForm(vector<int>& A, int k) {
        vector<int> ans;
        for(int i=A.size()-1;i>=0;i--){
            A[i] += k;

            k = A[i]/10;
            ans.push_back(A[i]%10);
        }

        while(k){
           ans.push_back(k%10);
            k/=10;
        }
        reverse(ans.begin(), ans.end());
        return ans;

    }
};
