class Solution {
public:
    vector<int> advantageCount(vector<int>& nums1, vector<int>& nums2) {
       multiset<int> s(begin(nums1), end(nums1));

        vector<int> ans;
        for(auto i : nums2) {
            auto p  = (*s.rbegin() <= i) ? s.begin() : s.upper_bound(i);
            ans.push_back(*p);
            s.erase(p);
        }
        return  ans;
    }
};
