class Solution {
public:

    map<int, vector<int> > g;
    vector<int> v;
    vector<vector<int> > ans;
    map<int, bool> vis;
    void dfs(int src, int n)  {
        if(src == n - 1) {
            // v.push_back(n - 1);
            ans.push_back(v);
            // v.pop_back();
            return;
        }

        vis[src] = true;
        for(auto i : g[src]) {
            if(!vis[i]) {
               v.push_back(i);
               dfs(i, n);
               v.pop_back();
            }
        }

        vis[src] = false;

    }
    vector<vector<int>> allPathsSourceTarget(vector<vector<int>>& graph) {
        int n = graph.size();
        for(int i = 0; i < n; i++) {
            for(auto j: graph[i]) {
                g[i].push_back(j);
            }
        }
        v.push_back(0);
        dfs(0, n);
        return ans;
    }
};
