class Solution {
public:
    int alternateDigitSum(int n) {
        string s=to_string(n);

        int ans=0;
        bool p=1;
        for(auto i:s){
            if(p){
                ans+=i-'0';
            }else{
                ans-=i-'0';
            }
            p=!p;
        }
        return ans;
    }
};
