class Solution {
public:
    int appendCharacters(string s, string t) {
        int ss=0,tt=0,count=0;
        while(ss<s.size()){
            if(s[ss]==t[tt]){
                count++;
                ss++;
                tt++;
            }else{
                ss++;
            }
        }
        return t.size()-count;
    }
};
