class Solution {
public:
    long long ansF(int i,int n){
        long long ans=(long long )n*(2*i+(n-1))/2;
        return ans;
    }
    long long minimalKSum(vector<int>& nums, int k) {
        unordered_map<int,int> umap;
        sort(nums.begin(),nums.end());
        long long ans=0;
        int mx=0;
        int prev=1;
         for(int i=0;i<nums.size()&&k;i++){
             if(prev>nums[i] ) continue;
            if(nums[i]-prev<=k && prev!=nums[i] ){

                ans+=ansF(prev,nums[i]-prev);
                k-=nums[i]-prev;
            }else if(nums[i]-prev> k){
                for(int pk=prev;pk<nums[i]&&k;pk++){
                    ans+=pk;
                    k--;
                }
                return ans;
            }
             prev=nums[i]+1;

        }
        mx=nums[nums.size()-1];
        if(k!=0){
            ans+=(long long)k*(2*(mx+1)+(k-1))/2;
        }
        return ans;

    }
};
