class Solution {
    public boolean checkArray(int[] arr, int k) {
        int n = arr.length;
        for (int i = 0; i < n - k + 1; i++)
        {
            if(arr[i] <= 0)continue;
            int a = arr[i];
            for (int j = i; j < i + k; j++)
            {
                arr[j] -= a;
                if (arr[j] < 0)
                    return false;
            }
        }
        boolean b1 = true;
        for (int i = n - k + 1; i < n; i++)
            if (arr[i] != 0)
                b1 = false;
        return b1;
    }
}
