class Solution {
public:
    int numberOfArithmeticSlices(vector<int>& nums) {
        int n = nums.size();
        vector<int> dp(n, 0);
        int i = 2;
        int cnt  = 1;
        while(i < n) {
            if(nums[i] - nums[i-1] == nums[i-1] - nums[i-2]) {
                dp[i] = cnt + dp[i-1];
                cnt++;
            }
            else {
                dp[i] = dp[i-1];
                cnt = 1;
            }
            i++;
        }
        return dp[n-1];

    }
};
