class Solution {
public:
    int arrangeCoins(int n) {
        long long int lo = 0, hi = 1e9;
        long long int ans = 0;
        while(lo<=hi){
            long long int mid = (lo + hi)/2;
            long long int t = (mid * (mid + 1))/2;
            if(t == n)return mid;
            else if(t > n)hi = mid - 1;
            else lo = mid + 1;
        }
        return hi;
    }
};
