class Solution {
public:
    vector<int> asteroidCollision(vector<int>& ass) {
        stack<int> st;
        for(int i =0;i<ass.size();i++){
            while(!st.empty() && ass[i]<0 && st.top()>0){
                int sum = ass[i]+st.top();
                if(sum<0){
                    st.pop();
                }else if(sum>0){
                    ass[i]=0;

                }else{
                    st.pop();
                    ass[i]=0;
                }
            } 
            if(ass[i]!=0){
                st.push(ass[i]);
            }
        }
        vector<int> res;
        while(!st.empty()){
            res.push_back(st.top());
            st.pop();
        }
        reverse(res.begin(),res.end());
        return res;
    }
};
