class Solution {
public:
    int numRookCaptures(vector<vector<char>>& board) {
        int row;
        int col;
        // int cnt = 0;
        for(int i=0;i<8;i++){
            for(int j=0;j<8;j++){
                if(board[i][j] == 'R') {
                    row = i;
                    col = j;
                    // cnt = 1;
                    break;

                }

            }
            // if(cnt == 1)
                // break;
        }


        int cap = 0;

        for(int i = col;i >= 0;i--){
            if(board[row][i] == 'B') break;
            if(board[row][i] == 'p'){
                cap++;
                break;
            }
        }

        for(int i = col;i <= 7;i++){
            if(board[row][i] == 'B') break;
            if(board[row][i] == 'p'){
                cap++;
                break;
            }
        }

        for(int i = row;i >= 0;i--){
            if(board[i][col] == 'B') break;
            if(board[i][col] == 'p'){
                cap++;
                break;
            }
        }

        for(int i = row;i < 8;i++){
            if(board[i][col] == 'B') break;
            if(board[i][col] == 'p'){
                cap++;
                break;
            }
        }
        return cap;







    }
};
