class Solution {
public:
    double average(vector<int>& salary) {
        int _min = 1e9, _max = 0;
        long sum = 0;
        for(auto i : salary){
            _min = min(_min, i);
            _max = max(_max, i);
            sum += i;
        }
        return (sum - _min - _max*1.0)/(salary.size() - 2);
    }
};
