class Solution {
public:
    bool backspaceCompare(string s, string t) {
        string t1 = "", t2 = "";
        for(auto i : s){
            if(i == '#'){
                if(!t1.empty())t1.pop_back();
            }
            else t1.push_back(i);
        }

        for(auto i : t){
            if(i == '#'){
                if(!t2.empty())t2.pop_back();
            }
            else t2.push_back(i);
        }
        return (t1 == t2);
    }
};
