class Solution {
public:
    string convertToBase7(int num) {
        if(num == 0)return "0";
        int sign = num>=0?0:1;
        num = abs(num);
        string temp = "";
        while(num > 0){
            temp.push_back('0' + num%7);
            num/=7;
        }
        if(sign)temp.push_back('-');
        reverse(temp.begin(), temp.end());
        return temp;
    }
};
