class Solution {
public:
    int calPoints(vector<string>& operations) {
        stack<int> st;
        for(auto i : operations){
            if(i == "+"){
                int b = st.top();
                st.pop();
                int a = st.top();
                st.pop();
                st.push(a);
                st.push(b);
                st.push(a + b);
            }else if(i == "D"){
                int b = st.top();
                st.pop();
                st.push(b);
                st.push(2 * b);
            }else if(i == "C"){
                st.pop();
            }else{
                int n = stoi(i);
                st.push(n);
            }
        }
        int sum = 0;
        while(!st.empty()){
            sum += st.top();
            st.pop();
        }
        return sum;
    }
};
