class Solution {
public:
    int calculate(string s) {
        stack<int> st;
        int sign = 1, sum = 0;
        int n = s.length();
        for(int i=0;i<s.length();i++)
        {
            if(s[i]>='0' && s[i]<='9')
            {
                int num=0;
                while(n>i && s[i]>='0' && s[i]<='9')
                {
                    num =num*10 +(s[i]-'0');
                    i++;
                }
                sum+=num*sign;
                i--;
            }
            else if(s[i]=='+')
            {
                sign=1;
            }
            else if(s[i]=='-')
            {
                sign=-1;
            }
            else if(s[i]=='(')
            {
                st.push(sum);
                st.push(sign);
                sum=0;
                sign=1;
            }
            else if(s[i]==')')
            {
                sum=st.top()*sum;
                st.pop();
                sum+=st.top();
                st.pop();
            }
        }
        return sum;
    }
};
