class Solution {
public:
    vector<int> constructArray(int n, int k) {
        int i = 1;
        int j = n;
        vector<int> ans;
        int turn = 0;
        while(k-- >= 1) {
            if(!turn) {
                ans.push_back(i);
                i++;
            }
            else {
                ans.push_back(j);
                j--;
            }
            turn = !turn;
        }

        if(turn) {
            for(int k =  i; k <= j; k++) {
                ans.push_back(k);
            }

        }
        else {
            for(int k = j; k >= i; k--) {
                ans.push_back(k);
            }
        }
        return ans; 
    }
};
