class Solution {
public:
    string bestHand(vector<int>& ranks, vector<char>& suits) {
        unordered_map<int, int> rank;
        unordered_map<char, int> suit;
        for(auto i:  ranks) rank[i]++;
        for(auto i : suits) suit[i]++;

        for(auto i  : suit) {
            if(i.second == 5) 
            {
                return "Flush";
            }
        }
        for(auto i : rank) {
            if(i.second >= 3) {
                return "Three of a Kind";
            }
        }

         for(auto i : rank) {
            if(i.second >= 2) {
                return "Pair";
            }
        }

        return "High Card";
    }
};
