int dp[50005][2];
class Solution {
public:
    int helper(vector<int> &nums, int ind, int flag){
        if(ind == nums.size()){
            return -1e8;
        }
        if(dp[ind][flag] != -1)return dp[ind][flag];
        if(flag){
            int buy = helper(nums, ind + 1, 0) + nums[ind] + ind;
            int notBuy = helper(nums, ind + 1, 1);
            return dp[ind][flag] = max(buy, notBuy);
        }else{
            int sell = nums[ind] - ind;
            int notSell = helper(nums, ind + 1, 0);
            return dp[ind][flag] = max(sell, notSell);
        }
    }
    int maxScoreSightseeingPair(vector<int>& values) {
        memset(dp, -1, sizeof(dp));
        return helper(values, 0, 1);
    }
};
