int dp[1005][2][105];
class Solution {
public:
    int helper(vector<int> &prices, int ind, int canBuy, int k){
        if(k < 0)return -1e8;

        if(ind == prices.size()){
            return 0;
        }
        if(dp[ind][canBuy][k] != -1)return dp[ind][canBuy][k];

        if(canBuy){
            int buy = helper(prices, ind + 1, 0, k) - prices[ind];
            int notBuy = helper(prices, ind + 1, 1, k);
            return dp[ind][canBuy][k] = max(buy, notBuy);
        }else{
            int sell = helper(prices, ind + 1, 1, k - 1) + prices[ind];
            int notSell = helper(prices, ind + 1, 0, k);
            return dp[ind][canBuy][k] = max(sell, notSell);
        }        
    }
    int maxProfit(int k, vector<int>& prices) {
        memset(dp, -1, sizeof dp);
        return helper(prices, 0, 1, k);        
    }
};
