int dp[5005][2];
class Solution {
public:
    int helper(vector<int> &prices, int ind, int canBuy){
        if(ind >= prices.size()){return 0;}
        if(dp[ind][canBuy] != -1)return dp[ind][canBuy];
        if(canBuy){
            int buy = helper(prices, ind + 1, 0) - prices[ind];
            int notBuy = helper(prices, ind + 1, 1);
            return dp[ind][canBuy] = max(buy, notBuy);
        }else{
            int sell = helper(prices, ind + 2, 1) + prices[ind];
            int notSell = helper(prices, ind + 1, 0);
            return dp[ind][canBuy] = max(sell, notSell);
        }
    }
    int maxProfit(vector<int>& prices) {
        memset(dp, -1, sizeof dp);
        return helper(prices, 0, 1);
    }
};
