int dp[50001][2];
class Solution {
public:
    int helper(vector<int> &prices, int &fee, int ind, int canBuy){
        if(ind == prices.size()){
            return 0;
        }
        if(dp[ind][canBuy] != -1)return dp[ind][canBuy];
        if(canBuy){
            int buy = helper(prices, fee, ind + 1, 0) - prices[ind] - fee;
            int notBuy = helper(prices, fee, ind + 1, 1);
            return dp[ind][canBuy] = max(buy, notBuy);
        }else{
            int sell = helper(prices, fee, ind + 1, 1) + prices[ind];
            int notSell = helper(prices, fee, ind + 1, 0);
            return dp[ind][canBuy] = max(sell, notSell);
        }
    }
    int maxProfit(vector<int>& prices, int fee) {
        memset(dp, -1, sizeof dp);
        return helper(prices, fee, 0, 1);
    }
};
