class Solution {
public:
    bool hasAlternatingBits(int n) {
        int dig = log2(n) + 1;
        for(int i = dig - 1;i>=0;i--){
            int prev = ((n & (1 << (i + 1)))) == 0?0:1;
            int curr = ((n & (1 << (i)))) == 0?0:1;
            if(curr != 1 - prev)return false;
        }
        return true;
    }
};
