class Solution {
public:
    int leastBricks(vector<vector<int>>& wall) {
        unordered_map<int,int> mp;
        int maxi = 0;

        for(int i =0;i<wall.size();i++){
           int edge =0;

            for(int j =0;j<wall[i].size()-1;j++){
                int current = wall[i][j];
                edge = current+edge;
                mp[edge]++;
                maxi = max(maxi,mp[edge]);
            }
        }
        return wall.size()-maxi;

    }
};
