class Solution {
public:
    int brokenCalc(int startValue, int target) {
        int t = target;
        int ans = 0;
        while(t > startValue) {
            ans++;
            if(t%2 == 0) {
                t /= 2;
            }
            else t += 1;
        }
        ans += (startValue - t);
        int _ans = 0;
        int S = startValue;
        while(S <= target) {
            _ans++;
            S *= 2;
        }
        _ans += (S - target);
        return min(ans, _ans);
    }
};
