class Solution {
public:
    string getHint(string secret, string guess) {
        unordered_map<int, int> umap1, umap2;
        for(auto i : secret){
            umap1[i]++;
        }
        for(auto i : guess){
            umap2[i]++;
        }
        int a = 0, b = 0;
        for(auto i : umap1){
            b += min(i.second, umap2[i.first]);
        }

        for(int i = 0; i < secret.size(); i++){
            if(secret[i] == guess[i]){
                a++;
                b--;
            }
        }
        string s = to_string(a) + "A" + to_string(b) + "B";
        return s;        
    }
};
