class Solution {
public:
    string find_sum(string s) {
        int ans = 0;
        for(auto i:s) {
            ans += (i - '0');
        }
        return to_string(ans);
    }
    string digitSum(string s, int k) {
        while(s.size() > k) {
            string t = "";
            while(s.size() >= k) {
                t += find_sum(s.substr(0, k));
                s = s.substr(k);
            }
            if(s.size() > 0) {
                t += find_sum(s);
            }
            s = t;
        }
        return s;
    }
};
