int dp[100005];
class Solution {
public:
    int helper(vector<int>&arr, int ind){
        if(ind >= arr.size()){
            return 0;
        }
        if(dp[ind] != -1)return dp[ind];
        if(arr[ind] == 1){
            return dp[ind] = helper(arr, ind + 2);
        }else{
            int take = 0;
            int notTake = helper(arr, ind + 1);
            if(ind != arr.size() - 1 && arr[ind + 1] == 1){
                take = helper(arr, ind + 1);
            }else{
                take = 1 + helper(arr, ind + 2);
            }
            return dp[ind] = max(take, notTake);
        }
    }
    bool canPlaceFlowers(vector<int>& flowerbed, int n) {
        memset(dp, -1, sizeof dp);
        int _max = helper(flowerbed, 0);
        return _max >= n;
    }
};
