class Solution {
public:
    int candy(vector<int> &arr)
    {
        vector<int> temp(arr.size(), 1);
        for (int i = 1; i < arr.size(); i++)
        {
            if(arr[i] > arr[i - 1]){
                temp[i] = temp[i - 1] + 1;
            }
        }
        for (int i = arr.size() - 1; i > 0; i--)
        {
            if(arr[i] < arr[i - 1]){
                temp[i - 1] = max(temp[i] + 1, temp[i - 1]);
            }
        }
        // for (auto i : temp)
        // {
        //     cout << i << ", ";
        // }
        return accumulate(temp.begin(), temp.end(), 0);
    }
};
