class Solution {
public:
int helper(int mid,vector<int> &weights,int days){
    int count = 1;
    int sum = 0;
    for(int i =0;i<weights.size();i++){
        sum+=weights[i];
        if(sum>mid){
            count++;
            sum = weights[i];
        }
    }if(count<=days){
        return true;
    }
    return false;
}
    int shipWithinDays(vector<int>& weights, int days) {
        int sum =0;
        int max = 0;
        for(auto i:weights){
            sum+=i;
        }
        int lo = *max_element(weights.begin(),weights.end());
        int hi = sum;
        int ans = 0;
        while(lo<=hi){
            int mid = (lo+hi)>>1;
            if(helper(mid,weights,days)){
                ans = mid;
                hi= mid-1;
            }else{
                lo = mid+1;
            }
        }
        return ans;
    }

};
