class Solution {
public:
    string cap(string s){
        for(int i = 0; i < s.length();i++){
            if(s[i] >= 'A' && s[i] <= 'Z'){
                s[i] = (s[i] - 'A' + 'a');
            }
        }
        if(s.length() > 2 && s[0] >= 'a')s[0] = s[0] - 'a' + 'A';
        return s;
    }
    string capitalizeTitle(string title) {
        string temp = "", ans = "";
        for(auto i : title){
            if(i == ' '){
                ans += cap(temp);
                ans.push_back(' ');
                temp = "";
            }else{
                temp.push_back(i);
            }
        }
        ans += cap(temp);
        return ans;
    }
};
