class Solution {
public:
    int carFleet(double t, vector<int>& p, vector<int>& s) {
        if(p.size() == 1) return 1;
        if(p.size() == 0) return 0;

        map<int, double> m;
        for(int i=0;i<p.size();i++) {
            m[-p[i]] = (double) (t-p[i])/s[i];
        }

        for(auto i : m) {
            cout<<i.first<<' '<<i.second<<endl;
        }

        int ans = 0;
        double curr = 0;
        for(auto it : m)  {
            if(it.second >  curr) {
                ans++;
                curr = it.second;
            } 
        }
        return  ans;

    }
};
