class Solution {
public:
    string categorizeBox(int l, int w, int h, int m) {
        int a=0,b=0;
        a=((l>=10000||w>=10000||h>=10000)||((long long)((long long)l*(long long)w*(long long)h)>=1e9))?1:0;
        b=(m>=100)? 1:0;
        if(a&&b) return "Both";
        else if(a) return "Bulky";
        else if(b) return "Heavy";
        return "Neither";

    }
};
