class Solution {
public:
    int oddCells(int n, int m, vector<vector<int>>& indices) {
        int dp[n][m];
        memset(dp,0,sizeof(dp));
        for(int i=0;i<indices.size();i++){
            int x = indices[i][0];            int y = indices[i][1];
            for(int j=0;j<m;j++){
                dp[x][j]++;
            }
             for(int j=0;j<n;j++){
                dp[j][y]++;
            }

        }
        int cnt=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(dp[i][j]%2!=0){
                    cnt++;
                }
            }
        }
        return cnt;
    }
};
