class Solution {
public:
    bool checkDistances(string s, vector<int>& distance) {
        map<int, vector<int>> mp;
        for(int i = 0; i < s.length(); i++){
            mp[s[i] -'a'].push_back(i);
        }
        for(auto i : mp){
            int dist = i.second[1] - i.second[0] - 1;
            if(distance[i.first] != dist)return false;
        }
        return true;
    }
};
