class Solution {
public:
    bool hasAllCodes(string s, int k) {
        unordered_set<string> st;
        int n = s.size();
        int size = (1 << k);
        for(int i = 0; i < n - k + 1; i++) {
            string t = s.substr(i, k);
            st.insert(t);
        }
        return st.size() == size;
    }
};
