class Solution {
public:
    int isPrefixOfWord(string t, string s) {
        if(s.size() > t.size()) {
            return -1;
        }
        int size = s.size();
        int cnt = 1;
        for(int i  = 0; i < t.size() - size + 1; i++) {
            string c = t.substr(i, size);
            if(t[i] == ' ') {
                cnt++;
            }
            if(c == s) {
                if((i-1 >= 0 and t[i-1] == ' ') || (i == 0))
                    return cnt;
            }
        }
        return -1;
    }
};
