class Solution {
public:
    bool areOccurrencesEqual(string s) {
        map<char, int> m;
        for(auto i : s)m[i]++;
        int _max = 0, _min = 1e9;
        for(auto i : m){
            _max = max(_max, i.second);
            _min = min(_min, i.second);
        }
        return (_min == _max);
    }
};
