class Solution {
public:
    bool isCovered(vector<vector<int>>& A, int left, int right) {
        vector<int> v;
        for(int i=left;i<=right;i++) v.push_back(i);

       set<int> s;
        for(int i=0;i<A.size();i++){
           for(int j=A[i][0];j<=A[i][1];j++) s.insert(j);
        }
       for(auto i : v){
           if(s.count(i) == 0) return false;
       }
        return true;
    }
};
