class Solution {
public:
    bool isGood(vector<int>& nums) {
        int n = nums.size() - 1;
        if(n == 0)return false;
        map<int, int> mp;
        for(auto i : nums)mp[i]++;
        for(int i = 1; i <=n ;i++){
            if(i != n){
                if(mp[i] != 1)return false;
            }else{
                if(mp[i] != 2)return false;
            }
        }
        return true;

    }
};
