class Solution {
public:
    bool canArrange(vector<int>& arr, int k) {
        int n = arr.size();
        unordered_map<int,int>mp;
        for(int i = 0;i<n;i++){
            int rem = arr[i]%k;
            mp[((arr[i] % k) + k) % k]++;
        }

        for(int i = 0;i<n;i++){
            int rem =((arr[i] % k) + k) % k;
            if(rem == 0){
                if(mp[rem]%2 == 1)return false;
            }else if(2*rem == k){
                if(mp[rem]%2 == 1)return false;
            }else{
                int fq = mp[rem];
                int ofq = mp[k-rem];
                if(fq!=ofq)return false;
            }
        }

        return true;
    }
};
