class Solution {
public:
    bool checkIfExist(vector<int>& arr) {
        map<int, int> m;
        for(auto i : arr)m[i]++;
        for(auto i : arr){
            if(m[2 * i] > 0){
                if(i == 0){
                    if(m[i] > 1)return true;
                }else{
                    return true;
                }

            }
        }
        return false;
    }
};
