class Solution {
public:
    bool digitCount(string num) {
        vector<int> arr(10);
        for(auto i : num){
            arr[i - '0']++;
        }
        for(int i = 0; i < num.length();i++){
            if(arr[i] != num[i] - '0'){
                return false;
            }
        }
        return true;
    }
};
