class Solution {
public:
    unordered_map<string,int> umap;
    unordered_set<string> vis;
    bool helper(int x,int y,int &X,int &Y){

        if(x>X||x<1||y<1||y>Y) return 0;

        cout<<x<<" "<<y<<endl;
        if(x==X&&y==Y) return 1;

        // string key=to_string(x)+" "+to_string(y);
        // if(vis.count(key)) return 0;
        // if(umap.count(key)) return umap[key];
        // vis.insert(key);
        bool ans=0;
        if(2*x>X){
            ans|=helper(x-y,y,X,Y);
        }else if(2*y>Y){
            ans|=helper(x,y-x,X,Y);
        }else{
            ans|=helper(2*x,y,X,Y)||helper(x,2*y,X,Y);
        }
        return ans;
    }
    bool isReachable(int X, int Y) {
        while(X!=1&&Y!=1){
            if(X<1||Y<1) return 0;
            while(X%2==0)
                 X/=2;

            while(Y%2==0)
                Y/=2;

            if(X==1||Y==1) return 1;

            if(X>Y) X-=Y;
            else Y-=X; 

        }
        return 1;
    }
};
